datetime \"created_at\"\n    t.datetime \"updated_at\"\n  end\n\n  create_table \"projects\", force: true do |t|\n    t.integer  \"project_id\"\n    t.integer  \"parent_id\"\n    t.string   \"name\"\n    t.string   \"body\"\n    t.string   \"icon\"\n    t.datetime \"started\"\n    t.datetime \"finished\"\n    t.datetime \"created_at\"\n    t.datetime \"updated_at\"\n  end\n\n  create_table \"users\", force: true do |t|\n    t.string   \"email\",                  default: \"\", null: false\n    t.string   \"encrypted_password\",     default: \"\", null: false\n    t.string   \"reset_password_token\"\n    t.datetime \"reset_password_sent_at\"\n    t.datetime \"remember_created_at\"\n    t.integer  \"sign_in_count\",          default: 0,  null: false\n    t.datetime \"current_sign_in_at\"\n    t.datetime \"last_sign_in_at\"\n    t.string   \"current_sign_in_ip\"\n    t.string   \"last_sign_in_ip\"\n    t.datetime \"created_at\"\n    t.datetime \"updated_at\"\n  end\n\n  add_index \"users\", [\"email\"], name: \"index_users_on_email\", unique: true, using: :btree\n  add_index \"users\", [\"reset_password_token\"], name: \"index_users_on_reset_password_token\", unique: true, using: :btree\n\nend\n","undoManager":{"mark":45,"position":46,"stack":[[{"group":"doc","deltas":[{"start":{"row":45,"column":23},"end":{"row":45,"column":24},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":51,"column":21},"end":{"row":52,"column":0},"action":"insert","lines":["",""]},{"start":{"row":52,"column":0},"end":{"row":52,"column":4},"action":"insert","lines":["    "]}]}],[{"group":"doc","deltas":[{"start":{"row":52,"column":4},"end":{"row":52,"column":5},"action":"insert","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":52,"column":5},"end":{"row":52,"column":6},"action":"insert","lines":["."]}]}],[{"group":"doc","deltas":[{"start":{"row":52,"column":6},"end":{"row":52,"column":7},"action":"insert","lines":["d"]}]}],[{"group":"doc","deltas":[{"start"